  	let rank = "B";
  	switch(rank){
    	case "S":
      		console.log("あなたは天才だわ♪");
      		break; //これがないと以降の処理も実行されてしまう。
    	case "A":
      		console.log("あなたは優秀だわ");
      		break;
    	case "B":
      		console.log("あなた、凡人ね");
